   254→## src/hooks/useDashboardData.ts
   255→```
   256→import { useState, useEffect, useMemo, useCallback } from 'react';
   257→import type { ArtikelEinstellen } from '@/types/app';
   258→import { LivingAppsService } from '@/services/livingAppsService';
   259→
   260→export function useDashboardData() {
   261→  const [artikelEinstellen, setArtikelEinstellen] = useState<ArtikelEinstellen[]>([]);
   262→  const [loading, setLoading] = useState(true);
   263→  const [error, setError] = useState<Error | null>(null);
   264→
   265→  const fetchAll = useCallback(async () => {
   266→    setError(null);
   267→    try {
   268→      const [artikelEinstellenData] = await Promise.all([
   269→        LivingAppsService.getArtikelEinstellen(),
   270→      ]);
   271→      setArtikelEinstellen(artikelEinstellenData);
   272→    } catch (err) {
   273→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   274→    } finally {
   275→      setLoading(false);
   276→    }
   277→  }, []);
   278→
   279→  useEffect(() => { fetchAll(); }, [fetchAll]);
   280→
   281→  return { artikelEinstellen, setArtikelEinstellen, loading, error, fetchAll };
   282→}
