   299→
   300→/** Extract display label from lookup/select or lookup/radio API response */
   301→export function displayLookup(val: unknown): string {
   302→  if (val == null) return '—';
   303→  if (typeof val === 'object' && val !== null && 'label' in val) return String((val as Record<string, unknown>).label);
   304→  return String(val);
   305→}
   306→
   307→/** Extract labels from multiplelookup/checkbox API response */
   308→export function displayMultiLookup(val: unknown): string {
   309→  if (val == null) return '—';
   310→  if (Array.isArray(val)) return val.map(v => typeof v === 'object' && v !== null && 'label' in v ? String((v as Record<string, unknown>).label) : String(v)).join(', ') || '—';
   311→  return displayLookup(val);
   312→}
   313→
   314→/** Extract key from lookup value (handles both {key,label} objects and plain strings) */
   315→export function lookupKey(val: unknown): string | undefined {
   316→  if (val == null) return undefined;
   317→  if (typeof val === 'object' && val !== null && 'key' in val) return String((val as Record<string, unknown>).key);
   318→  if (typeof val === 'string') return val;
   319→  return undefined;
   320→}
   321→
   322→/** Extract keys from multiplelookup value */
   323→export function lookupKeys(val: unknown): string[] {
   324→  if (!Array.isArray(val)) return [];
   325→  return val.map(v => typeof v === 'object' && v !== null && 'key' in v ? String((v as Record<string, unknown>).key) : String(v));
   326→}
